-- Insert academy memberships for all NEW contacts (excluding existing user)
SELECT 
    p.id,
    p.academy_id,
    p.role,
    CASE 
        WHEN p.membership_status = 'active' THEN true
        ELSE false
    END
FROM public.profiles p
WHERE p.email != 'bobby@groundstandard.com'  -- Exclude existing user
  AND NOT EXISTS (
      SELECT 1 FROM public.academy_memberships am 
      WHERE am.user_id = p.id AND am.academy_id = p.academy_id
  );